import React, { Component } from "react";
import Footer from "../layout/Footer";
import Header from "../layout/Header";
import Sidebar from "../layout/Sidebar";
import './WelcomePage.css';
import Chart from "react-apexcharts";
import moment from 'moment';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend
} from "recharts";
const axios = require("axios").default;


class WelcomePage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      centre: [],
      project: [],
      filterData: {
        centre_name: "",
        project_name: "",
        year:"",
      },
      isCheckedProjectWiseFund:false,
      totalFunding: 0,
      totalProject: 0,
      totalEmployee: 0,
      knowledgeProduct: 0,
      totalMou: 0,

      financeList: [],
      lastAmtDate: "",
      lastAmtReceived: "",
      totalReceivedAmt: "",
      balanceAmt: "",
      amountAllocatedTillDate: 0,
      expenditureTillDate: 0,

      //series: [44, 55, 13, 43, 22],
      series: [],
      options: {},

      series1: [],
      options1: {},

      // for second row graph
      series2Row: [],
      options2Row: {
        chart: {
          height: 350,
          type: 'radialBar',
        },
        plotOptions: {
          radialBar: {
            hollow: {
              size: '70%',
            }
          },
        },
        labels: ['MOU'],
        colors: ['#FFA556'],
      },

      series22Row: [],
      options22Row: {
        chart: {
          height: 350,
          type: 'radialBar',
        },
        plotOptions: {
          radialBar: {
            hollow: {
              size: '70%',
            }
          },
        },
        labels: ['Geographic'],
        colors: ['#AC86D1'],
      },

      series23Row: [],
      options23Row: {
        chart: {
          height: 350,
          type: 'radialBar',
        },
        plotOptions: {
          radialBar: {
            hollow: {
              size: '70%',
            }
          },
        },
        labels: ['Advocacy'],
        colors: ['#9BEA81'],

      },

      series24Row: [],
      options24Row: {
        chart: {
          height: 350,
          type: 'radialBar',
        },
        plotOptions: {
          radialBar: {
            hollow: {
              size: '70%',
            }
          },
        },
        labels: ['Knowledge'],
        colors: ['#208293']
      },

      series25Row: [],
      options25Row: {
        chart: {
          height: 350,
          type: 'radialBar',
        },
        plotOptions: {
          radialBar: {
            hollow: {
              size: '70%',
            }
          },
        },
        labels: ['Other'],
      },

      series3Row: [{
        data: [21, 22, 10, 28, 16, 5, 8]
      }],
      options3Row: {
        chart: {
          height: 350,
          type: 'bar',
          events: {
            click: function(chart, w, e) {
              // console.log(chart, w, e)
            }
          }
        },
        // colors: colors,
        plotOptions: {
          bar: {
            columnWidth: '45%',
            distributed: true,
          }
        },
        dataLabels: {
          enabled: false
        },
        legend: {
          show: false
        },
        xaxis: {
          categories: [
            ['Account', 'Department'],
            ['Information', 'Technology'],
            ['Marketing', 'Department'],
            ['Customer', 'Success'],
            'Finance',
            'R&D',
            'Sales',
          ],
          labels: {
            style: {
              // colors: colors,
              fontSize: '12px'
            }
          }
        }
      },

    };
    this.handleChange = this.handleChange.bind(this);
    this.reloadDashboard = this.reloadDashboard.bind(this);
    this.handleCheckboxChange = this.handleCheckboxChange.bind(this);
    this.formattedNumber = this.formattedNumber.bind(this);
    this.myfunction = this.myfunction.bind(this);
  }

   // set state year, all center, all Project
   handleChange(e) {
    let filterData = this.state.filterData;
    filterData[e.target.name] = e.target.value;
    this.setState({ filterData: filterData });
  }

   handleCheckboxChange = async() => {
    let thizz = this;
   await this.setState((prevState) => ({
      isCheckedProjectWiseFund: !prevState.isCheckedProjectWiseFund, // Toggle the checkbox state
    }));
    let myObj ={
      isChecked: thizz.state.isCheckedProjectWiseFund
    }
    axios({
      method: "post",
      url: process.env.REACT_APP_BASE_URL + "/api/landing-page/project-wise-funding",
      data: myObj
    }).then((response)=> {
      var actualResp = response.data;
      if (actualResp.FundingAgency) {
        let newOptions = {
          chart: {
            width: 380,
            type: 'pie',
          },
          labels: actualResp.FundingAgency,
          responsive: [{
            breakpoint: 480,
            options: {
              chart: {
                width: 200,
              },
              legend: {
                position: 'bottom'
              }
            }
          }]
        }
        thizz.setState({ options: newOptions })
      }
      if (actualResp.FundingAmt) {
        thizz.setState({ series: actualResp.FundingAmt })
      }
    }).catch((err)=> {
      console.log(err);
    })


