import React, { Component } from "react";
import Footer from "../layout/Footer";
import Header from "../layout/Header";
import Sidebar from "../layout/Sidebar";
import { Link } from "react-router-dom";
import Chart from "react-apexcharts";

const axios = require("axios").default;

class ProjectActivityGraph extends Component {

  constructor(props) {
    super(props);
    this.state = {
      series: [0.36], // Adjusted value for 36% coverage
      options: {
        chart: {
          height: 350,
          type: 'radialBar',
        },
        plotOptions: {
          radialBar: {
            hollow: {
              margin: 15,
              size: '70%',
            },
            dataLabels: {
              show: true,
              name: {
                offsetY: -10,
                show: true,
                color: '#888',
                fontSize: '17px',
              },
              value: {
                color: '#111',
                fontSize: '36px',
                show: true,
                offsetY: 8,
              },
              total: {
                show: true,
                label: 'Total',
                color: '#888',
                fontSize: '20px',
              },
            },
          },
        },
        labels: ['Coverage'],
      },
    };
  }

  render() {

    return (
      <div className="donut">
        <Chart
          options={this.state.options}
          series={this.state.series}
          type="radialBar"
          width="380"
        />
      </div>
    );
  }
}

export default ProjectActivityGraph;
