import React, { Component } from "react";
import MarksBarChart from "./MarksBarChart";

class BarChart extends Component {
  // Sample data for total marks and obtained marks for each subject
  state = {
    data: {
      labels: ["Math", "Science", "History", "English", "Art"],
      datasets: [
        {
          label: "Total Marks",
          data: [100, 90, 95, 80, 85],
          backgroundColor: "rgba(54, 162, 235, 0.6)",
        },
        {
          label: "Obtained Marks",
          data: [90, 80, 75, 70, 80],
          backgroundColor: "rgba(255, 99, 132, 0.6)",
        },
      ],
    },
  };

  render() {
    const { data } = this.state;

    return (
      <div>
        <h1>Marks Bar Chart</h1>
        <MarksBarChart data={data} />
      </div>
    );
  }
}

export default BarChart;
