import React, { Component } from "react";
import Footer from "../layout/Footer";
import Header from "../layout/Header";
import Sidebar from "../layout/Sidebar";
import "./WelcomePage.css";
import Chart from "react-apexcharts";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
} from "recharts";
const axios = require("axios").default;

class WelcomePage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      centre: [],
      project: [],
      centre_name: "",
      project_name: "",
      totalFunding: 0,
      totalProject: 0,
      totalEmployee: 0,
      knowledgeProduct: 0,
      totalMou: 0,

      //series: [44, 55, 13, 43, 22],
      series: [],
      options: {},

      series1: [],
      options1:{},

// Second row graph implementation
      options2: {
        plotOptions: {
          radialBar: {
            track: {
              background: "#f2f2f2",
              strokeWidth: "90%"
            },
            dataLabels: {
              show: true,
              value: {
                show: true,
                fontSize: "16px"
              },
              total: {
                show: true,
                fontSize: "10px",
                label: "",
                color: "#373d3f"
              }
            }
          }
        },
        colors: ['#FFA556'],

      },
      series2: [],
      catagoryName2: '',
      activity2: "",

      options2: {
        plotOptions: {
          radialBar: {
            track: {
              background: "#f2f2f2",
              strokeWidth: "90%"
            },
            dataLabels: {
              show: true,
              value: {
                show: true,
                fontSize: "16px"
              },
              total: {
                show: true,
                fontSize: "10px",
                label: "",
                color: "#373d3f"
              }
            }
          }
        },
        colors: ['#FFA556'],

      },
      series3: [],
      catagoryName3: '',
      activity3: "",

      options4: {
        plotOptions: {
          radialBar: {
            track: {
              background: "#f2f2f2",
              strokeWidth: "90%"
            },
            dataLabels: {
              show: true,
              value: {
                show: true,
                fontSize: "16px"
              },
              total: {
                show: true,
                fontSize: "10px",
                label: "",
                color: "#373d3f"
              }
            }
          }
        },
        colors: ['#FFA556'],

      },
      series4: [],
      catagoryName4: '',
      activity4: "",

      options5: {
        plotOptions: {
          radialBar: {
            track: {
              background: "#f2f2f2",
              strokeWidth: "90%"
            },
            dataLabels: {
              show: true,
              value: {
                show: true,
                fontSize: "16px"
              },
              total: {
                show: true,
                fontSize: "10px",
                label: "",
                color: "#373d3f"
              }
            }
          }
        },
        colors: ['#FFA556'],

      },
      series5: [],
      catagoryName5: '',
      activity5: "",

      options2: {
        plotOptions: {
          radialBar: {
            track: {
              background: "#f2f2f2",
              strokeWidth: "90%"
            },
            dataLabels: {
              show: true,
              value: {
                show: true,
                fontSize: "16px"
              },
              total: {
                show: true,
                fontSize: "10px",
                label: "",
                color: "#373d3f"
              }
            }
          }
        },
        colors: ['#FFA556'],

      },
      series6: [],
      catagoryName6: '',
      activity6: "",
    };
  }

  // get api
  componentDidMount() {
    var thizz = this;

    axios
      .get(process.env.REACT_APP_BASE_URL + "/api/landing-page")
      .then((response) => {
        console.log(response.data);

        var centre = response.data.centres;
        var project = response.data.projects;

        this.setState({
          centre: centre,
          project: project,
        });

        var actualResp = response.data;
        if (actualResp.totalFund) {
          thizz.setState({ totalFunding: actualResp.totalFund });
        }
        if (actualResp.totalProject) {
          thizz.setState({ totalProject: actualResp.totalProject });
        }
        if (actualResp.totalEmployee) {
          thizz.setState({ totalEmployee: actualResp.totalEmployee });
        }
        if (actualResp.totalKnowledgeProducts) {
          thizz.setState({
            knowledgeProduct: actualResp.totalKnowledgeProducts,
          });
        }
        if (actualResp.totalMou) {
          thizz.setState({ totalMou: actualResp.totalMou });
        }
        if (actualResp.FundingAgency) {
          let newOptions = {
            chart: {
              width: 380,
              type: "pie",
            },
            labels: actualResp.FundingAgency,
            responsive: [
              {
                breakpoint: 480,
                options: {
                  chart: {
                    width: 200,
                  },
                  legend: {
                    position: "bottom",
                  },
                },
              },
            ],
          };
          thizz.setState({ options: newOptions });
        }
        if (actualResp.FundingAmt) {
          thizz.setState({ series: actualResp.FundingAmt });
        }

        if ( actualResp.sanctionFund && actualResp.releasedFund && actualResp.utilizationFund
        ) {
          let newSeries = [
            {
              name: "Sanction Fund",
              data: actualResp.sanctionFund,
            },
            {
              name: "Released Fund",
              data: actualResp.releasedFund,
            },
            {
              name: "Utilization Fund",
              data: actualResp.utilizationFund,
            },
          ];
          thizz.setState({ series1: newSeries });
        }

        if (actualResp.projectList) {
          let secondOpt = {
            chart: {
              type: "bar",
              height: 350,
            },
            plotOptions: {
              bar: {
                horizontal: true,
                columnWidth: "55%",
                endingShape: "rounded",
              },
            },
            dataLabels: {
              enabled: false,
            },
            stroke: {
              show: true,
              width: 2,
              colors: ["transparent"],
            },
            xaxis: {
              categories: actualResp.projectList,
            },
            yaxis: {
              title: {
                text: "In crores",
              },
            },
            fill: {
              opacity: 1,
            },
            tooltip: {
              y: {
                formatter: function(val) {
                  return "&#x20B9; " + val + " crores";
                },
              },
            },
          };
          thizz.setState({ options1: secondOpt });
        }

        var series2 = thizz.state.series2;
        series2 = response.data.projectActivity2[0].series;
        var catagoryName2 = thizz.state.catagoryName2;
        catagoryName2 = response.data.projectActivity2[0].name;
        var activity2 = thizz.state.activity2;
        activity2 = response.data.projectActivity2[0].label

        var series3 = thizz.state.series3;
        series3 = response.data.projectActivity3[0].series;
        var catagoryName3 = thizz.state.catagoryName3;
        catagoryName3 = response.data.projectActivity3[0].name;
        var activity3 = thizz.state.activity3;
        activity3 = response.data.projectActivity3[0].label


        var series4 = thizz.state.series4;
        series4 = response.data.projectActivity4[0].series;
        var catagoryName4 = thizz.state.catagoryName4;
        catagoryName4 = response.data.projectActivity4[0].name;
        var activity4 = thizz.state.activity4;
        activity4 = response.data.projectActivity4[0].label

        var series5 = thizz.state.series5;
        series5 = response.data.projectActivity5[0].series;
        var catagoryName5 = thizz.state.catagoryName5;
        catagoryName5 = response.data.projectActivity5[0].name;
        var activity5 = thizz.state.activity5;
        activity5 = response.data.projectActivity5[0].label

        var series6 = thizz.state.series6;
        series6 = response.data.projectActivity6[0].series;
        var catagoryName6 = thizz.state.catagoryName6;
        catagoryName6 = response.data.projectActivity6[0].name;
        var activity6 = thizz.state.activity6;
        activity6 = response.data.projectActivity6[0].label



        thizz.setState({
          series2: series2,
          catagoryName2: catagoryName2,
          activity2: activity2,
          series3: series3,
          catagoryName3: catagoryName3,
          activity3: activity3,
          series4: series4,
          catagoryName4: catagoryName4,
          activity4: activity4,
          series5: series5,
          catagoryName5: catagoryName5,
          activity5: activity5,
          series6: series6,
          catagoryName6: catagoryName6,
          activity6: activity6,
        })
      })
      .catch((error) => {
        console.log(error.response.data);
      });
  }

  render() {
    let centre = this.state.centre.map(function(center, index) {
      return (
        <option key={center.id} value={center.centre_code}>
          {center.centre_name}
        </option>
      );
    });
    let project = this.state.project.map(function(project, index) {
      return (
        <option key={project.id} value={project.project_code}>
          {project.project_name}
        </option>
      );
    });

    return (
      <>
        <Header />
        <Sidebar />
        <div className="content-wrapper">
          <section className="content">
            <div className="heading">
              <span className="headingLine"> DIRECTOR NIUA DASHBOARD </span>
              <span className="filterButton">
                <div className="myContainer">
                  <select
                    className="form-control select-box"
                    name="centre_name"
                    id="centre_name"
                    value={this.state.centre_name}
                    // onChange={this.handleChange}
                  >
                    <option> All Centers </option>
                    {centre}
                  </select>

                  <select
                    className="form-control select-box"
                    name="project_name"
                    id="project_name"
                    value={this.state.project_name}
                    // onChange={this.handleChange}
                  >
                    <option selected="selected"> All Projects </option>
                    {project}
                  </select>
                </div>
              </span>
            </div>
            {/* <div className="container-fluid"> */}
            <div className="header-container card">
              <div className="icon">
                <i className="fa fa-bar-chart faChart" aria-hidden="true"></i>
              </div>
              <h6 className="header">Overview</h6>
              <div className="row text-center mt-4">
                <div className="col-md-2">
                  <p className="p1">Total Fund</p>
                  <p className="p2"> {this.state.totalFunding} </p>
                </div>
                <div className="col-md-2">
                  <p className="p1">Total Project</p>
                  <p className="p2"> {this.state.totalProject} </p>
                </div>
                <div className="col-md-2">
                  <p className="p1">Total Employee</p>
                  <p className="p2"> {this.state.totalEmployee} </p>
                </div>
                <div className="col-md-2">
                  <p className="p1">Knowledge Products</p>
                  <p className="p2"> {this.state.knowledgeProduct} </p>
                </div>
                <div className="col-md-2">
                  <p className="p1">Total MOU</p>
                  <p className="p2"> {this.state.totalMou} </p>
                </div>
              </div>
            </div>

            <div className="row">
              <div className="col-md-6">
                <div className="header-container card">
                  <div className="icon">
                    <i
                      className="fa fa-bar-chart faChart"
                      aria-hidden="true"
                    ></i>
                  </div>
                  <h4 className="header"> Overall Fund</h4>
                  <Chart
                    options={this.state.options}
                    series={this.state.series}
                    type="pie"
                    style={{ height: "315px" }}
                  />
                </div>
              </div>
              <div className="col-md-6">
                <div className="header-container card">
                  <div className="icon">
                    <i
                      className="fa fa-bar-chart faChart"
                      aria-hidden="true"
                    ></i>
                  </div>
                  <h4 className="header">Fund Utilization</h4>
                  <Chart
                    options={this.state.options1}
                    series={this.state.series1}
                    type="bar"
                    height={300}
                  />
                </div>
              </div>
            </div>

            <div className="row text-center p-2">
              <div className="col-md-2 card bg-primary">
                <p style={{ marginTop: "70px" }}> Progress </p>
                <p style={{ marginTop: "100px" }}> Activity </p>
              </div>

              <div className="col-md-2 card">
                <Chart
                  options={this.state.options2}
                  series={this.state.series2}
                  type="radialBar"
                  width="250"
                  className="setGraph"
                />
                <p> {this.state.catagoryName2} </p>
                <br />
                <br />
                <p className="bg-secondary p-1"> {this.state.activity2}</p>
              </div>
              <div
                className="col-md-2 card"
                style={{ backgroundColor: "#F4F6F9" }}
              >
                <Chart
                  options={this.state.options3}
                  series={this.state.series3}
                  type="radialBar"
                  width="250"
                  className="setGraph"
                />
                <p> {this.state.catagoryName3} </p>
                <br />
                <p className="bg-secondary p-1"> {this.state.activity3}</p>
              </div>

              <div className="col-md-2 card">
                <Chart
                  options={this.state.options4}
                  series={this.state.series4}
                  type="radialBar"
                  width="250"
                  className="setGraph"
                />
                <p> {this.state.catagoryName4} </p>
                <br />
                <p className="bg-secondary p-1"> {this.state.activity4}</p>
              </div>

              <div
                className="col-md-2 card"
                style={{ backgroundColor: "#F4F6F9" }}
              >
                <Chart
                  options={this.state.options5}
                  series={this.state.series5}
                  type="radialBar"
                  width="250"
                  className="setGraph"
                />
                <p> {this.state.catagoryName5} </p>
                <br />
                <p className="bg-secondary p-1"> {this.state.activity5}</p>
              </div>

              <div className="col-md-2 card">
                <Chart
                  options={this.state.options6}
                  series={this.state.series6}
                  type="radialBar"
                  width="250"
                  className="setGraph"
                />
                <p className=""> {this.state.catagoryName6} </p>
                <p className="bg-secondary p-1"> {this.state.activity6}</p>
              </div>
            </div>
          </section>
        </div>
        <Footer />
      </>
    );
  }
}

export default WelcomePage;
