import React, { Component } from "react";
import Footer from "../layout/Footer";
import Header from "../layout/Header";
import Sidebar from "../layout/Sidebar";
import { Link } from "react-router-dom";
import Chart from "react-apexcharts";

const axios = require("axios").default;

class ProjectActivityGraph extends Component {

  constructor(props) {
    super(props);
    this.state = {
      series: [36], // Set your percentage value here
      options: {
        chart: {
          height: 350,
          type: 'radialBar',
        },
        plotOptions: {
          radialBar: {
            hollow: {
              margin: 15,
              size: '70%',
            },
            dataLabels: {
              name: {
                show: false,
              },
              value: {
                show: true,
                fontSize: '30px',
                fontWeight: 'bold',
              },
              total: {
                show: true,
                label: 'Total',
                fontSize: '18px',
                fontWeight: 'bold',
                formatter: function (w) {
                  return w.globals.seriesTotals.reduce((a, b) => a + b, 0) + '%'; // Calculate and display total percentage
                },
              },
            },
          },
        },
        labels: ['Percentage'],
      },
    };
  }

  render() {

    return (
      <div className="donut">
        <Chart
          options={this.state.options}
          series={this.state.series}
          type="radialBar"
          width="380"
        />
      </div>
    );
  }
}

export default ProjectActivityGraph;
