import React, { Component } from "react";
import Footer from "../layout/Footer";
import Header from "../layout/Header";
import Sidebar from "../layout/Sidebar";

class ProjectPlan extends Component {
  constructor(props) {
    super(props);
    this.state = {
      singleFields: {
        centre_name: "",
        project_name: "",
        project_head: "",
        project_code: "",
        project_brief: "",
        Work_order: "",
        funding_agency: "",
        nodal_officer: "",
        contact_no: "",
        allocated_budget: "",
        start_date: "",
        end_date: "",
        overall_progress: "",
      },
      team_strength: [
        {
          team: "",
          position: "",
          experience: "",
          qualification: "",
          salary_slab: "",
        },
      ],
      finances: [
        {
          year: "",
          budget_head: "",
          allocated_fund: "",
          milestone: "",
        },
      ],
      project_activities: [
        {
          type: "",
          start_date: "",
          end_date: "",
          duration: "",
          activities: "",
          Remarks: "",
        },
      ],

      other_activities: [
        {
          other_activity: "",
          other_date: "",
        },
      ],
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleAddRow = this.handleAddRow.bind(this);
    this.handleChangeMul = this.handleChangeMul.bind(this);
  }

  // For simple fields
  handleChange(e) {
    let singleField = this.state.singleFields;
    singleField[e.target.name] = e.target.value;
    this.setState({ singleFields: singleField });
  }

  // SET MULTIPLE ROW
  handleChangeMul(idx, e, type) {
    console.log(idx, e.target.name, type);
    if (type === "teamStrength") {
      const { name, value } = e.target;
      const team_strength = this.state.team_strength;
      team_strength[idx][name] = value;
      this.setState({
        team_strength: team_strength,
      });
    } else if(type === "finances") {
      const { name, value } = e.target;
      const finances = this.state.finances;
      finances[idx][name] = value;
      this.setState({
        finances: finances,
      });
    }
  }

  handleRemoveSpecificRow(idx, type) {
    if (this.state.team_strength.length > 1 && type === "teamStrength") {
      const team_strength = this.state.team_strength;
      team_strength.splice(idx, 1);
      this.setState({ team_strength: team_strength });
    }
  }

  handleAddRow(type) {
    // console.log('type of value '+type);
    if (type === "teamStrength") {
      const item = {
        team: "",
        position: "",
        experience: "",
        qualification: "",
        salary_slab: "",
      };
      this.setState({
        team_strength: [...this.state.team_strength, item],
      });
    } else if (type === "empDetails") {
      const item = {
        emp_name: "",
        emp_designation: "",
        emp_detail: "",
        emp_from: "",
        emp_to: "",
        emp_leaving: "",
      };
      this.setState({
        emp_details: [...this.state.emp_details, item],
      });
    } else if (type === "educationDetails") {
      const item = {
        edu_name: "",
        edu_university: "",
        edu_year: "",
        edu_percentage: "",
      };
      this.setState({
        edu_details: [...this.state.edu_details, item],
      });
    } else if (type === "professionalRefrence") {
      const item = {
        ref_name: "",
        ref_designation: "",
        ref_organizationName: "",
        ref_contactNo: "",
        ref_email: "",
      };
      this.setState({
        professional_ref: [...this.state.professional_ref, item],
      });
    } else if (type === "nomineeDetails") {
      const item = {
        nominee_name: "",
        nominee_relation: "",
        nominee_dob: "",
        nominee_amtShare: "",
        nominee_minority: "",
      };
      this.setState({
        nominee_details: [...this.state.nominee_details, item],
      });
    } else if (type === "selfDeclaration") {
      const item = {
        selfDeclarationName: "",
        selfDeclarationDate: "",
        selfDeclarationFir: "",
        selfDeclarationAction: "",
        selfDeclarationStatus: "",
      };
      this.setState({
        selfDeclaration: [...this.state.selfDeclaration, item],
      });
    }
  }
  render() {
    return (
      <>
        <Header />
        <Sidebar />
        <div className="content-wrapper">
          {/* Content Header (Page header) */}
          <div className="content-header">
            <div className="container-fluid">
              <div className="row">
                <div className="col-md-12">
                  <div className="card card-primary">
                    <div className="card-header">
                      <h3 className="card-title"> Project Form-I </h3>
                    </div>
                    <form>
                      <div className="card-body">
                        <div className="row">
                          <div className="col-md-6">
                            <div className="form-group">
                              <label htmlFor="centreName">Centre Name</label>
                              <select
                                class="form-control "
                                name="centre_name"
                                id="centre_name"
                                value={this.state.singleFields.centre_name}
                                onChange={this.handleChange}
                              >
                                <option selected="selected"> select </option>
                                <option data-select2-id="35">Alaska</option>
                              </select>
                            </div>
                          </div>
                          <div className="col-md-6">
                            <div className="form-group">
                              <label htmlFor="projectName">Project Name</label>
                              <select
                                class="form-control "
                                name="project_name"
                                id="project_name"
                                value={this.state.singleFields.project_name}
                                onChange={this.handleChange}
                              >
                                <option selected="selected"> select </option>
                                <option data-select2-id="35">Alaska</option>
                              </select>
                            </div>
                          </div>
                        </div>
                        <div className="row">
                          <div className="col-md-6">
                            <div className="form-group">
                              <label htmlFor="projectHeadName">
                                Project Head Name
                              </label>
                              <select
                                class="form-control "
                                name="project_head"
                                id="project_head"
                                value={this.state.singleFields.project_head}
                                onChange={this.handleChange}
                              >
                                <option selected="selected"> select </option>
                                <option data-select2-id="35">Alaska</option>
                              </select>
                            </div>
                          </div>
                          <div className="col-md-6">
                            <div className="form-group">
                              <label htmlFor="ProjectCode">Project Code</label>
                              <input
                                type="text"
                                className="form-control"
                                id="project_code"
                                name="project_code"
                                placeholder="Project Code"
                                value={this.state.singleFields.project_code}
                                onChange={this.handleChange}
                              />
                            </div>
                          </div>
                        </div>
                        <div className="row ">
                          <div className="form-group col-md-6">
                            <label for="projectBrief" className="p">
                              Project Brief
                            </label>
                            <textarea
                              className="form-control "
                              id="project_brief"
                              name="project_brief"
                              placeholder="Project Brief"
                              value={this.state.singleFields.project_brief}
                              onChange={this.handleChange}
                            ></textarea>
                          </div>

                          <div className="form-group col-md-6">
                            <label for="workOrderNo" className="p">
                              Work Order No
                            </label>
                            <input
                              type="text"
                              className="form-control"
                              id="Work_order"
                              name="Work_order"
                              placeholder="Work Order No"
                              value={this.state.singleFields.Work_order}
                              onChange={this.handleChange}
                            />
                          </div>
                        </div>

                        <div className="row ">
                          <div className="form-group col-md-6">
                            <label for="fundingMinistry/Agency/Dept">
                              Funding Ministry/Agency/Dept.
                            </label>
                            <select
                              class="form-control "
                              name="funding_agency"
                              id="funding_agency"
                              value={this.state.singleFields.funding_agency}
                              onChange={this.handleChange}
                            >
                              <option selected="selected"> select </option>
                              <option data-select2-id="35">Alaska</option>
                            </select>
                          </div>

                          <div className="form-group col-md-6">
                            <label for="nodalOfficerName" className="p">
                              Nodal Officer Name
                            </label>
                            <input
                              type="text"
                              className="form-control "
                              id="nodal_officer"
                              name="nodal_officer"
                              placeholder="Nodal Officer Name"
                              value={this.state.singleFields.nodal_officer}
                              onChange={this.handleChange}
                            />
                          </div>
                        </div>
                        <div className="row ">
                          <div className="form-group col-md-6">
                            <label for="contactNo">Contact no.</label>
                            <input
                              className="form-control"
                              id="contact_no"
                              name="contact_no"
                              placeholder="Contact No"
                              value={this.state.singleFields.contact_no}
                              onChange={this.handleChange}
                            />
                          </div>

                          <div className="form-group col-md-6">
                            <label for="allocated_budget">
                              Allocated Budget
                            </label>
                            <input
                              type="text"
                              className="form-control"
                              id="allocated_budget"
                              name="allocated_budget"
                              placeholder="Allocated Budget"
                              value={this.state.singleFields.allocated_budget}
                              onChange={this.handleChange}
                            />
                          </div>
                        </div>
                        <div className="row">
                          <div className="form-group col-md-6">
                            <label for="startDate">Start Date</label>
                            <input
                              type="date"
                              name="start_date"
                              id="start_date"
                              class="form-control"
                              value={this.state.singleFields.start_date}
                              onChange={this.handleChange}
                            />
                          </div>
                          <div className="form-group col-md-6">
                            <label for="endDate">End Date</label>
                            <input
                              type="date"
                              name="end_date"
                              id="end_date"
                              class="form-control"
                              value={this.state.singleFields.end_date}
                              onChange={this.handleChange}
                            />
                          </div>
                        </div>
                        <div className="row ">
                          <div className="form-group col-md-6">
                            <label for="overall_progress">
                              Overall Progress
                            </label>
                            <input
                              className="form-control"
                              id="overall_progress"
                              name="overall_progress"
                              placeholder="Overall Progress"
                              value={this.state.singleFields.overall_progress}
                              onChange={this.handleChange}
                            />
                          </div>
                        </div>
                        <p className="lead"> Team Strength </p>
                        <table className="table">
                          <thead className="thead-dark">
                            <tr>
                              <th>Team Strength</th>
                              <th>Position</th>
                              <th>Experience</th>
                              <th>Qualification</th>
                              <th>Salary Slab</th>
                              <th style={{ width: "100px" }}>Action</th>
                            </tr>
                          </thead>
                          <tbody id="tbl">
                            {this.state.team_strength.map((item, idx) => (
                              <tr key={"ts-" + idx}>
                                <td>
                                  <input
                                    type="text"
                                    className="form-control"
                                    id="team"
                                    placeholder="Team strength"
                                    name="team"
                                    onChange={(evnt) =>this.handleChangeMul(idx,evnt,"teamStrength")}
                                    value={item.team}
                                  />
                                </td>
                                <td>
                                  <input
                                    type="text"
                                    className="form-control"
                                    id="position"
                                    placeholder="Position"
                                    name="position"
                                    onChange={ (evnt) =>this.handleChangeMul(idx,evnt,"teamStrength") }
                                    value={item.position}
                                  />
                                </td>
                                <td>
                                  <input
                                    type="text"
                                    className="form-control"
                                    id="experience"
                                    placeholder="Experience"
                                    name="experience"
                                    onChange={ (evnt) =>this.handleChangeMul(idx,evnt,"teamStrength") }
                                    value={item.experience}
                                  />
                                </td>
                                <td>
                                  <input
                                    type="text"
                                    className="form-control"
                                    id="qualification"
                                    placeholder="Qualification"
                                    name="qualification"
                                    onChange={ (evnt) =>this.handleChangeMul(idx,evnt,"teamStrength") }
                                    value={item.qualification}
                                  />
                                </td>
                                <td>
                                  <input
                                    type="text"
                                    className="form-control"
                                    id="salary_slab"
                                    placeholder="Salary slab"
                                    name="salary_slab"
                                    onChange={ (evnt) =>this.handleChangeMul(idx,evnt,"teamStrength") }
                                    value={item.salary_slab}
                                  />
                                </td>
                                <td>
                                  <button
                                    className="btn btn-danger btn-sm m-1"
                                    type="button"
                                    onClick={() =>
                                      this.handleAddRow("teamStrength")
                                    }
                                  >
                                    +
                                  </button>
                                  <button
                                    type="button"
                                    className="btn btn-success btn-sm"
                                    onClick={() =>
                                      this.handleRemoveSpecificRow(
                                        idx,
                                        "teamStrength"
                                      )
                                    }
                                  >
                                    -
                                  </button>
                                </td>
                              </tr>
                            ))}
                          </tbody>
                        </table>

                        <p className="lead"> Finances </p>
                        <table className="table">
                          <thead className="thead-dark">
                            <tr>
                              <th>Year</th>
                              <th>Budget Head</th>
                              <th>Allocated Fund</th>
                              <th>Milestone</th>
                              <th style={{ width: "100px" }}>Action</th>
                            </tr>
                          </thead>
                          <tbody id="tbl2">
                            {this.state.finances.map((item, idx)=> (
                               <tr key={"fin-" + idx}>
                               <td>
                                 <input
                                   type="text"
                                   className="form-control"
                                   id="year"
                                   placeholder="Year"
                                   name="year"
                                   onChange={ (evnt) =>this.handleChangeMul(idx,evnt,"finances") }
                                    value={item.year}
                                 />
                               </td>
                               <td>
                                 <select
                                   class="form-control"
                                   name="budget_head"
                                   id="budget_head"
                                   onChange={ (evnt) =>this.handleChangeMul(idx,evnt,"finances") }
                                    value={item.budget_head}
                                 >
                                   <option selected="selected"> select </option>
                                   <option value="hr cost">HR Cost</option>
                                   <option value="travel cost">
                                     Travel Cost
                                   </option>
                                   <option value="infrastructure cost">
                                     Infrastructure Cost
                                   </option>
                                   <option value="Miscellaneous cost">
                                     Miscellaneous Cost
                                   </option>
                                   <option value="a&o">A&O</option>
                                 </select>
                               </td>
                               <td>
                                 <input
                                   type="text"
                                   className="form-control"
                                   id="allocated_fund"
                                   placeholder="Allocated Fund"
                                   name="allocated_fund"
                                   onChange={ (evnt) =>this.handleChangeMul(idx,evnt,"finances") }
                                    value={item.allocated_fund}
                                 />
                               </td>
                               <td>
                                 <input
                                   type="text"
                                   className="form-control"
                                   id="milestone"
                                   placeholder="Milestone"
                                   name="milestone"
                                   onChange={ (evnt) =>this.handleChangeMul(idx,evnt,"finances") }
                                    value={item.milestone}
                                 />
                               </td>
                               <td>
                                 <button
                                   className="btn btn-danger btn-sm m-1"
                                   type="button"
                                 >
                                   +
                                 </button>
                                 <button
                                   type="button"
                                   className="btn btn-success btn-sm"
                                 >
                                   -
                                 </button>
                               </td>
                             </tr>
                            ))}
                           
                          </tbody>
                        </table>

                        <p className="lead"> Project Activities </p>
                        <table className="table">
                          <thead className="thead-dark">
                            <tr>
                              <th style={{ width: "230px" }}>Type</th>
                              <th>Start Date</th>
                              <th>End Date</th>
                              <th>
                                {" "}
                                Duration <small>in days</small>
                              </th>
                              <th style={{ width: "200px" }}>Activities</th>
                              <th style={{ width: "200px" }}> Remarks</th>
                              <th style={{ width: "100px" }}>Action</th>
                            </tr>
                          </thead>
                          <tbody>
                            <tr>
                              <td>
                                <select
                                  class="form-control select2 select2-hidden-accessible"
                                  name="type"
                                  id="type"
                                >
                                  <option selected="selected">select</option>
                                  <option value="major">Major</option>
                                  <option value="critical">Critical</option>
                                  <option value="remaining">Remaining</option>
                                </select>
                              </td>
                              <td>
                                <input
                                  type="date"
                                  className="form-control"
                                  id="start_date"
                                  name="start_date"
                                />
                              </td>
                              <td>
                                <input
                                  type="date"
                                  className="form-control"
                                  id="end_date"
                                  name="end_date"
                                />
                              </td>
                              <td>
                                <input
                                  type="text"
                                  className="form-control"
                                  name="duration"
                                  id="duration"
                                />
                              </td>
                              <td>
                                <textarea
                                  className="form-control "
                                  id="activities"
                                  name="activities"
                                  placeholder="Activities"
                                ></textarea>
                              </td>
                              <td>
                                <textarea
                                  className="form-control "
                                  id="remarks"
                                  name="remarks"
                                  placeholder="Remarks"
                                ></textarea>
                              </td>
                              <td>
                                <button
                                  className="btn btn-danger btn-sm m-1"
                                  type="button"
                                >
                                  +
                                </button>
                                <button
                                  type="button"
                                  className="btn btn-success btn-sm"
                                >
                                  -
                                </button>
                              </td>
                            </tr>
                          </tbody>
                        </table>

                        <p className="lead"> Other Activities </p>
                        <table className="table">
                          <thead className="thead-dark">
                            <tr>
                              <th> Activities </th>
                              <th>Date</th>

                              <th style={{ width: "100px" }}>Action</th>
                            </tr>
                          </thead>
                          <tbody>
                            <tr>
                              <td>
                                <input
                                  type="text"
                                  className="form-control"
                                  id="other_activity"
                                  placeholder="Team strength"
                                  name="other_activity"
                                />
                              </td>
                              <td>
                                <input
                                  type="date"
                                  className="form-control"
                                  id="other_date"
                                  placeholder="Position"
                                  name="other_date"
                                />
                              </td>

                              <td>
                                <button
                                  className="btn btn-danger btn-sm m-1"
                                  type="button"
                                >
                                  +
                                </button>
                                <button
                                  type="button"
                                  className="btn btn-success btn-sm"
                                >
                                  {" "}
                                  -{" "}
                                </button>
                              </td>
                            </tr>
                          </tbody>
                        </table>
                      </div>
                      <div className="card-footer">
                        <button type="button" className="btn btn-primary">
                          Submit
                        </button>
                      </div>
                    </form>
                  </div>
                </div>
              </div>

              {/* ------ */}
              {/* /.row */}
            </div>
            {/* /.container-fluid */}
          </div>
        </div>
        <aside className="control-sidebar control-sidebar-dark">
          {/* Control sidebar content goes here */}
        </aside>

        <Footer />
      </>
    );
  }
}

export default ProjectPlan;
