import React, { Component } from "react";
import Footer from "../layout/Footer";
import Header from "../layout/Header";
import Sidebar from "../layout/Sidebar";
import { Link } from "react-router-dom";
import Chart from "react-apexcharts";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend
} from "recharts";
import  './styles.css';
const axios = require("axios").default;

class ProjectActivityGraph extends Component {

  constructor(props) {
    super(props);
    this.state = {
      years: [],
      selectedYear: "",
      
        options: {
          plotOptions: {
            radialBar: {
              track: {
                background: "#f2f2f2",
                strokeWidth: "90%"
              },
              dataLabels: {
                show: true,
                value: {
                  show: true,
                  fontSize: "16px"
                },
                total: {
                  show: true,
                  fontSize: "10px",
                  label: "",
                  color: "#373d3f"
                }
              }
            }
          },
          colors: ['#FFA556'], 
           
        },
        series: [],
        catagoryName:'',
        activity:"",

        options2: {
          plotOptions: {
            radialBar: {
              track: {
                background: "#f2f2f2",
                strokeWidth: "90%"
              },
              dataLabels: {
                show: true,
                value: {
                  show: true,
                  fontSize: "16px"
                },
                total: {
                  show: true,
                  fontSize: "10px",
                  label: "",
                  color: "#373d3f"
                }
              }
            }
          },
          colors: ['#AC86D1'],
          
        },
        series2: [],
        catagoryName2:'',
        activity2:"",

        options3: {
          plotOptions: {
            radialBar: {
              track: {
                background: "#f2f2f2",
                strokeWidth: "90%"
              },
              dataLabels: {
                show: true,
                value: {
                  show: true,
                  fontSize: "16px"
                },
                total: {
                  show: true,
                  fontSize: "10px",
                  label: "",
                  color: "#373d3f"
                }
              }
            }
          },
          colors: ['#9BEA81'],
          
        },
        series3: [],
        catagoryName3:'',
        activity3:"",

        options4: {
          plotOptions: {
            radialBar: {
              track: {
                background: "#f2f2f2",
                strokeWidth: "90%"
              },
              dataLabels: {
                show: true,
                value: {
                  show: true,
                  fontSize: "16px"
                },
                total: {
                  show: true,
                  fontSize: "10px",
                  label: "",
                  color: "#373d3f"
                }
              }
            }
          },
          colors: ['#208293'],  
        },
        series4: [],
        catagoryName4:'',
        activity4:"",

        options5: {
          plotOptions: {
            radialBar: {
              track: {
                background: "#f2f2f2",
                strokeWidth: "90%"
              },
              dataLabels: {
                show: true,
                value: {
                  show: true,
                  fontSize: "16px"
                },
                total: {
                  show: true,
                  fontSize: "10px",
                  label: "",
                  color: "#373d3f"
                }
              }
            }
          },
          
        },
        series5: [],
        catagoryName5:'',
        activity5:"",
      
    };
   

  }

  componentDidMount() {
    var thizz = this;
    let projectId = 60

    axios.get(process.env.REACT_APP_BASE_URL + "/api/project-activity-graph/" + projectId)
      .then((response) => {
       
        var options = thizz.state.options;
        options.plotOptions.radialBar.dataLabels.total.label = response.data.projectActivity[0].label;

        var series = thizz.state.series;
        series = response.data.projectActivity[0].series;

        var catagoryName = thizz.state.catagoryName;
        catagoryName = response.data.projectActivity[0].name;

        var activity = thizz.state.activity;
        activity = response.data.projectActivity[0].label

        var series2 = thizz.state.series2;
        series2 = response.data.projectActivity2[0].series;
        var catagoryName2 = thizz.state.catagoryName2;
        catagoryName2 = response.data.projectActivity2[0].name;
        var activity2 = thizz.state.activity2;
        activity2 = response.data.projectActivity2[0].label

        var series3 = thizz.state.series3;
        series3 = response.data.projectActivity3[0].series;
        var catagoryName3 = thizz.state.catagoryName3;
